\n    }\n}\n\n// æ¶ˆé™¤æ»¡è¡Œ\nfunction clearLines(&$board) {\n    $board = array_values(array_filter($board, function($row) {\n        return in_array(0, $row);\n    }));\n    while (count($board) < 20) {\n        array_unshift($board, array_fill(0, 10, 0));\n    }\n}\n\n// æ—‹è½¬æ–¹å—\nfunction rotate($shape) {\n    $rotated = [];\n    $rows = count($shape);\n    $cols = count($shape[0]);\n    for ($j = 0; $j < $cols; $j++) {\n        $newRow = [];\n        for ($i = $rows - 1; $i >= 0; $i--) {\n            $newRow[] = $shape[$i][$j];\n        }\n        $rotated[] = $newRow;\n    }\n    return $rotated;\n}\n\n// æ‰“å°æ£‹ç›˜\nfunction printBoard($board, $block = null) {\n    $display = $board;\n    if ($block) {\n        $shape = $block[\'shape\'];\n        $x = $block[\'x\'];\n        $y = $block[\'y\'];\n        foreach ($shape as $i => $row) {\n            foreach ($row as $j => $cell) {\n                if ($cell && isset($display[$y + $i][$x + $j])) {\n                    $display[$y + $i][$x + $j] = 2;\n                }\n            }\n        }\n    }\n    foreach ($display as $row) {\n        foreach ($row as $cell) {\n            echo $cell ? ($cell == 2 ? \"[]\" : \"##\") : \"  \";\n        }\n        echo \"\\n\";\n    }\n    echo str_repeat(\"-\", 20) . \"\\n\";\n}\n\n// ä¸»å¾ªç¯\n$block = newBlock($shapes);\nwhile (true) {\n    printBoard($board, $block);\n    // æ¨¡æ‹Ÿä¸‹è½\n    if (canPlace($board, $block, $block[\'x\'], $block[\'y\'] + 1)) {\n        $block[\'y\']++;\n    } else {\n        placeBlock($board, $block);\n        clearLines($board);\n        $block = newBlock($shapes);\n        if (!canPlace($board, $block, $block[\'x\'], $block[\'y\'])) {\n            echo \"Game Over!\\n\";\n            break;\n        }\n    }\n    // è¿™é‡Œå¯ä»¥æ·»åŠ ç”¨æˆ·è¾“å…¥æ§åˆ¶å·¦å³ç§»åŠ¨å’Œæ—‹è½¬\n    // ä¾‹å¦‚ï¼š$block[\'x\']++, $block[\'x\']--, $block[\'shape\'] = rotate($block[\'shape\']);\n    usleep(200000); // 0.2ç§’\n}\n?>\n```\n\n**è¯´æ˜ï¼š**\n- ä½