INSERT INTO `messages` (`id`, `conversation_id`, `role`, `content`, `tokens`, `model_name`, `file_id`, `created_at`) VALUES ('2a6eb101-179e-4dac-8aad-27b07d300f26','a28f770d-5328-496c-a44c-f5ecaf183091','assistant','å½“ç„¶å¯ä»¥ï¼ä¸‹é¢æ˜¯ä¸€ä¸ªç®€å•çš„â€œæ‰“ç –å—â€æ¸¸æˆçš„å®Œæ•´HTMLæ–‡ä»¶ï¼Œä½¿ç”¨äº†HTML5çš„`<canvas>`å…ƒç´ å’ŒJavaScriptæ¥å®ç°ã€‚ä½ å¯ä»¥ç›´æ¥å¤åˆ¶ä»¥ä¸‹ä»£ç åˆ°ä¸€ä¸ª`.html`æ–‡ä»¶ä¸­ï¼Œç„¶åç”¨æµè§ˆå™¨æ‰“å¼€å³å¯ç©æ¸¸æˆã€‚\n\n```html\n<!DOCTYPE html>\n<html lang=\"zh-CN\">\n<head>\n  <meta charset=\"UTF-8\" />\n  <title>æ‰“ç –å—æ¸¸æˆ</title>\n  <style>\n    body {\n      background: #222;\n      display: flex;\n      justify-content: center;\n      align-items: center;\n      height: 100vh;\n      margin: 0;\n      color: #fff;\n      font-family: \"å¾®è½¯é›…é»‘\", sans-serif;\n      user-select: none;\n    }\n    canvas {\n      background: #000;\n      display: block;\n      border: 2px solid #fff;\n      border-radius: 8px;\n    }\n    #score {\n      position: absolute;\n      top: 20px;\n      left: 50%;\n      transform: translateX(-50%);\n      font-size: 20px;\n      font-weight: bold;\n    }\n    #message {\n      position: absolute;\n      top: 60px;\n      left: 50%;\n      transform: translateX(-50%);\n      font-size: 24px;\n      font-weight: bold;\n      color: #ff4444;\n      display: none;\n    }\n  </style>\n</head>\n<body>\n  <div id=\"score\">å¾—åˆ†: 0</div>\n  <div id=\"message\"></div>\n  <canvas id=\"gameCanvas\" width=\"480\" height=\"320\"></canvas>\n\n  <script>\n    const canvas = document.getElementById(\"gameCanvas\");\n    const ctx = canvas.getContext(\"2d\");\n\n    // çƒçš„å±æ€§\n    const ballRadius = 10;\n    let x = canvas.width / 2;\n    let y = canvas.height - 30;\n    let dx = 3;\n    let dy = -3;\n\n    // çƒæ‹å±æ€§\n    const paddleHeight = 10;\n    const paddleWidth = 75;\n    let paddleX = (canvas.width - paddleWidth) / 2;\n\n    // ç –å—å±æ€§\n    const brickRowCount = 5;\n    const brickColumnCount = 7;\n    c