;\n            --bg-white: #FFFFFF;\n            --bg-card: #FFFFFF;\n            --border-color: #E0E0E0;\n            \n            /* å®ç”¨å˜é‡ */\n            --spacing-sm: 1rem;\n            --spacing-md: 1.5rem;\n            --spacing-lg: 2rem;\n            --spacing-xl: 3rem;\n            --radius-sm: 6px;\n            --radius-md: 12px;\n            --radius-lg: 18px;\n            --shadow-sm: 0 2px 8px rgba(0, 0, 0, 0.05);\n            --shadow-md: 0 5px 15px rgba(0, 0, 0, 0.07);\n            --shadow-lg: 0 10px 25px rgba(0, 0, 0, 0.1);\n            --transition: 0.3s ease;\n        }\n        \n        /* ===== ã€å›ºå®šã€‘èƒŒæ™¯æ•ˆæœ ===== */\n        .bg-particles {\n            position: fixed;\n            top: 0;\n            left: 0;\n            width: 100%;\n            height: 100%;\n            overflow: hidden;\n            background: linear-gradient(135deg, #FFFFFF, #F8F8F8);\n            z-index: -10;\n        }\n        \n        canvas#particleCanvas,\n        canvas#knowledgeGraph {\n            position: absolute;\n            top: 0;\n            left: 0;\n            width: 100%;\n            height: 100%;\n            opacity: 0.8;\n        }\n        \n        canvas#particleCanvas {\n            z-index: -9;\n        }\n        \n        canvas#knowledgeGraph {\n            z-index: -8;\n        }\n        \n        /* ===== ã€å›ºå®šã€‘é¡¶éƒ¨å¯¼èˆª ===== */\n        header {\n            background-color: transparent;\n            border-bottom: 1px solid rgba(158, 27, 50, 0.08);\n            position: relative;\n            z-index: 100;\n            padding: 0.5rem 0;\n        }\n        \n        .header-container {\n            display: flex;\n            align-items: center;\n            justify-content: space-between;\n            max-width: 1400px;\n            margin: 0 auto;\n            padding: 0 var(--spacing-lg);\n        }\n        \n        .institute-branding {\n            display: flex;\n            align-items: center;\n        }\n        \n        .logo-container {\n            width: 40px;\n            height: 40px;\n            border-radius: var(--radius-sm);\n            margin-right: var(--spacing-sm);\n            display: flex;\n            align-items: center;\n            justify-content: center;\n            background-color: var(--bg-white);\n            box-shadow: var(--shadow-sm);\n        }\n        \n        .institute-title {\n            color: var(--brand-primary);\n            font-size: 1rem;\n            font-weight: 700;\n            line-height: 1.2;\n        }\n        \n        /* ===== ä¸»å†…å®¹åŒºåŸŸ ===== */\n        .main-content {\n            flex: 1;\n            display: flex;\n            flex-direction: column;\n            position: relative;\n            overflow-x: hidden;\n            padding: 0 var(--spacing-md);\n        }\n        \n        .content-section {\n            max-width: 1200px;\n            width: 100%;\n            margin: 0 auto;\n            padding: var(--spacing-lg) var(--spacing-md);\n            position: relative;\n            z-index: 10;\n        }\n        \n        /* ===== ã€å›ºå®šã€‘åº•éƒ¨çŠ¶æ€æ  ===== */\n        .status-area {\n            background-color: transparent;\n            border-top: 1px solid var(--border-color);\n            padding: 0.4rem 0;\n            font-size: 0.75rem;\n            color: var(--text-secondary);\n            position: relative;\n            z-index: 100;\n        }\n        \n        .status-inner {\n            max-width: 1400px;\n            margin: 0 auto;\n            padding: 0 var(--spacing-lg);\n            display: flex;\n            justify-content: space-between;\n            align-items: center;\n        }\n        \n        .status-left {\n            display: flex;\n            align-items: center;\n            gap: var(--spacing-md);\n        }\n        \n        .copyright-area {\n            display: flex;\n            align-items: center;\n            gap: 0.8rem;\n        }\n        \n        .copyright-area a {\n            color: var(--brand-primary);\n            text-decoration: none;\n            transition: color var(--transition);\n        }\n        \n        .copyright-area a:hover {\n            color: var(--brand-light);\n            text-decoration: underline;\n        }\n        \n        /* ===== ç‰¹æ•ˆå’ŒåŠ¨ç”» ===== */\n        .ripple {\n            position: fixed;\n            border-radius: 50%;\n            background-color: rgba(158, 27, 50, 0.2);\n            transform: scale(0);\n            animation: ripple 1.5s ease-out;\n            pointer-events: none;\n            z-index: 1000;\n            width: 20px;\n            height: 20px;\n        }\n        \n        @keyframes ripple {\n            to {\n                transform: scale(3);\n                opacity: 0;\n            }\n        }\n        \n        /* ===== å“åº”å¼è®¾è®¡ ===== */\n        @media (max-width: 992px) {\n            .header-container, .status-inner {\n                padding: 0 1.5rem;\n            }\n        }\n        \n        @media (max-width: 768px) {\n            .header-container, .status-inner {\n                padding: 0 var(--spacing-sm);\n            }\n        }\n        \n        @media (max-width: 576px) {\n            .institute-title {\n                font-size: 0.9rem;\n            }\n            \n            .status-inner {\n                flex-direction: column;\n                gap: 0.5rem;\n                align-items: flex-start;\n            }\n        }\n        \n        /* ===== ä»¥ä¸‹æ˜¯å¯è‡ªç”±ä¿®æ”¹çš„å†…å®¹æ ·å¼ç»„ä»¶ ===== */\n        /* å¤§æ¨¡å‹å¯ä»¥æ ¹æ®éœ€è¦å¢åŠ ã€ä¿®æ”¹è¿™éƒ¨åˆ†æ ·å¼ */\n        .content-block {\n            margin-bottom: var(--spacing-lg);\n            animation: fade-in 0.5s ease-out;\n        }\n        \n        @keyframes fade-in {\n            from { opacity: 0; transform: translateY(20px); }\n            to { opacity: 1; transform: translateY(0); }\n        }\n        \n        .title-block {\n            margin-bottom: 1.5rem;\n            position: relative;\n        }\n        \n        .title-block h1 {\n            font-size: 2.5rem;\n            line-height: 1.2;\n            margin-bottom: 0.5rem;\n        }\n        \n        .title-block h2 {\n            font-size: 2rem;\n            line-height: 1.3;\n            margin-bottom: 0.5rem;\n        }\n        \n        .title-block h3 {\n            font-size: 1.75rem;\n            line-height: 1.4;\n            margin-bottom: 0.5rem;\n        }\n        \n        .text-block {\n            font-size: 1.1rem;\n            line-height: 1.6;\n            margin-bottom: 1.5rem;\n        }\n    </style>\n</head>\n\n<body>\n    <!-- ã€å›ºå®šã€‘èƒŒæ™¯æ•ˆæœ -->\n    <div class=\"bg-particles\">\n        <canvas id=\"particleCanvas\"></canvas>\n        <canvas id=\"knowledgeGraph\"></canvas>\n    </div>\n    \n    <!-- ã€å›ºå®šã€‘é¡¶éƒ¨å¯¼èˆª -->\n    <header>\n        <div class=\"header-container\">\n            <div class=\"institute-branding\">\n                <div class=\"logo-container\">\n                    <img src=\"https://www.ailabpku.com/uploads/lessons/13/1746686579877-logo.png\" alt=\"å®éªŒå®¤logo\" style=\"width: 100%; height: 100%; object-fit: contain;\" onerror=\"this.src=\'data:image/svg+xml;charset=utf-8,%3Csvg xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22 viewBox%3D%220 0 40 40%22%3E%3Crect width%3D%2240%22 height%3D%2240%22 fill%3D%22%239E1B32%22%2F%3E%3Ctext x%3D%2250%25%22 y%3D%2250%25%22 font-size%3D%2220%22 text-anchor%3D%22middle%22 fill%3D%22white%22 dy%3D%22.3em%22%3EPK%3C%2Ftext%3E%3C%2Fsvg%3E\'\">\n                </div>\n                <div class=\"institute-title\">åŒ—äº¬å¤§å­¦äººå·¥æ™ºèƒ½åº”ç”¨ä¸åˆ›æ–°å®éªŒå®¤</div>\n            </div>\n        </div>\n    </header>\n    \n    <!-- å¯è‡ªç”±å®šåˆ¶çš„å†…å®¹åŒºåŸŸ -->\n    <main class=\"main-content\">\n        <section class=\"content-section\">\n            <!-- å¤§æ¨¡å‹å¯ä»¥åœ¨è¿™é‡Œç”Ÿæˆè‡ªå®šä¹‰å†…å®¹ -->\n            <div class=\"content-block title-block\">\n                <h1><!-- è¯¾ç¨‹æ ‡é¢˜å°†åœ¨æ­¤å¤„æ’å…¥ --></h1>\n            </div>\n            \n            <div class=\"content-block text-block\">\n                <!-- è¯¾ç¨‹å†…å®¹å°†åœ¨æ­¤å¤„æ’å…¥ -->\n            </div>\n        </section>\n    </main>\n    \n    <!-- ã€å›ºå®šã€‘åº•éƒ¨çŠ¶æ€æ  -->\n    <div class=\"status-area\">\n        <div class=\"status-inner\">\n            <div class=\"status-left\">\n                <!-- ç§»é™¤äº†é¡µé¢è®¡æ•°æ¨¡å— -->\n            </div>\n            <div class=\"copyright-area\">\n                <span>Â© 2025 åŒ—äº¬å¤§å­¦äººå·¥æ™ºèƒ½åº”ç”¨ä¸åˆ›æ–°å®éªŒå®¤</span>\n                <a href=\"#\">ä½¿ç”¨æ¡æ¬¾</a>\n                <a href=\"#\">éšç§æ”¿ç­–</a>\n            </div>\n        </div>\n    </div>\n    \n    <!-- æ ¸å¿ƒè„šæœ¬ -->\n    <script>\n    document.addEventListener(\'DOMContentLoaded\', function() {\n        // ç»„ä»¶ç®¡ç†å™¨\n        const ComponentManager = {\n            components: {},\n            register: function(name, component) {\n                this.components[name] = component;\n                if (component.init) component.init();\n                return component;\n            },\n            get: function(name) {\n                return this.components[name];\n            }\n        };\n        \n        // æ€§èƒ½ç›‘æ§ç»„ä»¶\n        const PerformanceMonitor = {\n            isLowPerformance: false,\n            \n            init: function() {\n                this.checkPerformance();\n                return this;\n            },\n            \n            checkPerformance: function() {\n                const start = performance.now();\n                let sum = 0;\n                for (let i = 0; i < 200000; i++) {\n                    sum += i;\n                }\n                const end = performance.now();\n                \n                this.isLowPerformance = (end - start > 100);\n                \n                if (this.isLowPerformance) {\n                    document.documentElement.classList.add(\'low-performance\');\n                }\n                \n                return this.isLowPerformance;\n            }\n        };\n        \n        // ç²’å­ç³»ç»Ÿç»„ä»¶\n        const ParticleSystem = {\n            canvas: null,\n            ctx: null,\n            particles: [],\n            particleCount: 70,\n            \n            init: function() {\n                this.canvas = document.getElementById(\'particleCanvas\');\n                if (!this.canvas) return;\n                \n                this.ctx = this.canvas.getContext(\'2d\');\n                this.resizeCanvas();\n                this.initParticles();\n                \n                window.addEventListener(\'resize\', () => this.resizeCanvas());\n                \n                this.animate();\n                return this;\n            },\n            \n            resizeCanvas: function() {\n                if (!this.canvas) return;\n                this.canvas.width = window.innerWidth;\n                this.canvas.height = window.innerHeight;\n            },\n            \n            initParticles: function() {\n                this.particles = [];\n                \n                // æ ¹æ®æ€§èƒ½è°ƒæ•´ç²’å­æ•°é‡\n                if (ComponentManager.get(\'performanceMonitor\').isLowPerformance) {\n                    this.particleCount = 40;\n                }\n                \n                const particleColors = [\n                    \'rgba(158, 27, 50, 0.3)\',\n                    \'rgba(201, 43, 62, 0.2)\',\n                    \'rgba(122, 21, 37, 0.15)\',\n                    \'rgba(255, 90, 96, 0.1)\'\n                ];\n                \n                for (let i = 0; i < this.particleCount; i++) {\n                    this.particles.push({\n                        x: Math.random() * this.canvas.width,\n                        y: Math.random() * 