onst userClean = userAnswer.replace(/[ã€‚ï¼ï¼Ÿï¼Œã€ï¼›ï¼š\"\"\'\']/g, \'\');\n            const correctClean = correctAnswer.replace(/[ã€‚ï¼ï¼Ÿï¼Œã€ï¼›ï¼š\"\"\'\']/g, \'\');\n            \n            if (userClean === correctClean || userAnswer === correctAnswer) {\n                feedback.textContent = \"ğŸ‰ å¤ªæ£’äº†ï¼ç­”æ¡ˆæ­£ç¡®ï¼\";\n                feedback.className = \"feedback correct\";\n                practiceScore++;\n            } else {\n                feedback.textContent = `âŒ ç­”æ¡ˆä¸å¯¹å“¦ï¼æ­£ç¡®ç­”æ¡ˆæ˜¯ï¼š${correctAnswer}`;\n                feedback.className = \"feedback incorrect\";\n            }\n            \n            feedback.style.display = \"block\";\n            updatePracticeProgress();\n        }\n\n        // ä¸‹ä¸€é¢˜\n        function nextQuestion() {\n            currentQuestionIndex = (currentQuestionIndex + 1) % practiceQuestions.length;\n            document.getElementById(\'practiceQuestion\').innerHTML = practiceQuestions[currentQuestionIndex].question;\n            document.getElementById(\'userAnswer\').value = \'\';\n            document.getElementById(\'feedback\').style.display = \'none\';\n        }\n\n        // æ›´æ–°ç»ƒä¹ è¿›åº¦\n        function updatePracticeProgress() {\n            const progress = (practiceScore / practiceQuestions.length) * 100;\n            document.getElementById(\'practiceProgress\').style.width = progress + \'%\';\n            document.getElementById(\'practiceScore\').textContent = practiceScore;\n        }\n\n        // å¼€å§‹æ¸¸æˆ\n        function startGame() {\n            gameScore = 0;\n            currentGameIndex = 0;\n            document.getElementById(\'gameScore\').textContent = gameScore;\n            document.getElementById(\'startBtn\').style.display = \'none\';\n            showGameQuestion();\n        }\n\n        // æ˜¾ç¤ºæ¸¸æˆé¢˜ç›®\n        function showGameQuestion() {\n            if (currentGameIndex >= gameQuestions.length) {\n                endGame();\n                return;\n            }\n            \n            const question = gameQuestions[currentGameIndex];\n            document.getElementById(\'gameSentence\').textContent = question.question;\n            \n            const optionsContainer = document.getElementById(\'gameOptions\');\n            optionsContainer.innerHTML = \'\';\n            \n            question.options.forEach((option, index) => {\n                const optionDiv = document.createElement(\'div\');\n                optionDiv.className = \'option\';\n                optionDiv.textContent = option;\n                optionDiv.onclick = () => selectGameOption(index);\n                optionsContainer.appendChild(optionDiv);\n            });\n            \n            document.getElementById(\'nextBtn\').style.display = \'none\';\n        }\n\n        // é€‰æ‹©æ¸¸æˆé€‰é¡¹\n        function selectGameOption(selectedIndex) {\n            const question = gameQuestions[currentGameIndex];\n            const options = document.querySelectorAll(\'.option\');\n            \n            options.forEach((option, index) => {\n                option.onclick = null; // ç¦ç”¨ç‚¹å‡»\n                if (index === question.correct) {\n                    option.classList.add(\'correct\');\n                } else if (index === selectedIndex) {\n                    option.classList.add(\'incorrect\');\n                }\n            });\n            \n            if (selectedIndex === question.correct) {\n                gameScore += 20;\n                document.getElementById(\'gameScore\').textContent = gameScore;\n            }\n            \n            document.getElementById(\'nextBtn\').style.display = \'inline-block\';\n        }\n\n        // ä¸‹ä¸€ä¸ªæ¸¸æˆé¢˜ç›®\n        function nextGameQuestion() {\n            currentGameIndex++;\n            showGameQuestion();\n        }\n\n        // ç»“æŸæ¸¸æˆ\n        function endGame() {\n            document.getElementById(\'gameSentence\').innerHTML = `\n                ğŸŠ æ¸¸æˆç»“æŸï¼<br>\n                ä½ çš„æœ€ç»ˆå¾—åˆ†æ˜¯ï¼š${gameScore} åˆ†<br>\n                ${gameScore >= 80 ? \'å¤ªæ£’äº†ï¼ä½ æ˜¯å¥å¼æ”¹é€ å¤§å¸ˆï¼\' : \n                  gameScore >= 60 ? \'ä¸é”™å“¦ï¼ç»§ç»­åŠ æ²¹ï¼\' : \'å¤šå¤šç»ƒä¹ ï¼Œä½ ä¼šæ›´æ£’çš„ï¼\'}\n            `;\n            document.getElementById(\'gameOptions\').innerHTML = \'\';\n            document.getElementById(\'nextBtn\').style.display = \'none\';\n            document.getElementById(\'startBtn\').style.display = \'inline-block\';\n            document.getElementById(\'startBtn\').textContent = \'é‡æ–°å¼€å§‹\';\n        }\n\n        // é¡µé¢åŠ è½½æ—¶åˆå§‹åŒ–\n        window.onload = function() {\n            document.getElementById(\'practiceQuestion\').innerHTML = practiceQuestions[0].question;\n        };\n    </script>\n</body>\n</html>\n```\n\nè¿™ä¸ªæ™ºèƒ½è¯¾ä»¶å…·æœ‰ä»¥ä¸‹ç‰¹è‰²åŠŸèƒ½ï¼š\n\n## ğŸŒŸ ä¸»è¦ç‰¹ç‚¹ï¼š\n\n1. **ç”ŸåŠ¨çš„è§†è§‰è®¾è®¡**\n   - æ¸å˜èƒŒæ™¯å’ŒåŠ¨ç”»æ•ˆæœ\n   - å¯çˆ±çš„å¡é€šè§’è‰²\n   - ä¸°å¯Œçš„emojiå›¾æ ‡\n\n2. **å››å¤§å­¦ä¹ æ¨¡å—**\n   - ğŸ“š å­¦ä¹ å›­åœ°ï¼šè¯¦ç»†è®²è§£å¥å¼æ”¹é€ è§„åˆ™\n   - âœï¸ äº’åŠ¨ç»ƒä¹ ï¼šå®æ—¶ç»ƒä¹ å’Œåé¦ˆ\n   - ğŸ® è¶£å‘³æ¸¸æˆï¼šé€‰æ‹©é¢˜å½¢å¼çš„æŒ‘æˆ˜\n   - ğŸ“ çŸ¥è¯†æ€»ç»“ï¼šé‡ç‚¹çŸ¥è¯†å½’çº³\n\n3. **äº’åŠ¨åŠŸèƒ½**\n   - å®æ—¶ç­”æ¡ˆæ£€æµ‹\n   - è¿›åº¦è¿½è¸ª\n   - å³æ—¶åé¦ˆ\n   - åˆ†æ•°ç»Ÿè®¡\n\n4. **æ•™å­¦å†…å®¹**\n   - åé—®å¥ä¸é™ˆè¿°å¥äº’è½¬\n   - æ¸…æ™°çš„è½¬æ¢è§„åˆ™\n   - ä¸°å¯Œçš„å®ä¾‹æ¼”ç¤º\n   - è®°å¿†å£è¯€\n\n5. **é€‚åº”æ€§è®¾è®¡**\n   - å“åº”å¼å¸ƒå±€\n   - ç§»åŠ¨è®¾å¤‡å‹å¥½\n   - æµç•…çš„åŠ¨ç”»æ•ˆæœ\n\nè¿™ä¸ªè¯¾ä»¶é€šè¿‡æ¸¸æˆåŒ–çš„æ–¹å¼è®©äº”å¹´çº§å­¦ç”Ÿåœ¨è½»æ¾æ„‰å¿«çš„æ°›å›´ä¸­æŒæ¡å¥å¼æ”¹é€ çš„æŠ€å·§ï¼Œæ—¢æœ‰ç†è®ºå­¦ä¹ ï¼Œåˆæœ‰å®è·µç»ƒä¹ ï¼Œæ˜¯ä¸€ä¸ªå®Œæ•´çš„æ•™å­¦è§£å†³æ–¹æ¡ˆã€‚',7751,'anthropic/claude-sonnet-4',NULL,'2025-07-31 06:42:07');
