ap: 0.8rem;\n        }\n        \n        .copyright-area a {\n            color: var(--brand-primary);\n            text-decoration: none;\n            transition: color var(--transition);\n        }\n        \n        .copyright-area a:hover {\n            color: var(--brand-light);\n            text-decoration: underline;\n        }\n        \n        /* ===== ç‰¹æ•ˆå’ŒåŠ¨ç”» ===== */\n        .ripple {\n            position: fixed;\n            border-radius: 50%;\n            background-color: rgba(158, 27, 50, 0.2);\n            transform: scale(0);\n            animation: ripple 1.5s ease-out;\n            pointer-events: none;\n            z-index: 1000;\n            width: 20px;\n            height: 20px;\n        }\n        \n        @keyframes ripple {\n            to {\n                transform: scale(3);\n                opacity: 0;\n            }\n        }\n        \n        /* ===== å“åº”å¼è®¾è®¡ ===== */\n        @media (max-width: 992px) {\n            .header-container, .status-inner {\n                padding: 0 1.5rem;\n            }\n        }\n        \n        @media (max-width: 768px) {\n            .header-container, .status-inner {\n                padding: 0 var(--spacing-sm);\n            }\n        }\n        \n        @media (max-width: 576px) {\n            .institute-title {\n                font-size: 0.9rem;\n            }\n            \n            .status-inner {\n                flex-direction: column;\n                gap: 0.5rem;\n                align-items: flex-start;\n            }\n        }\n        \n        /* ===== ä»¥ä¸‹æ˜¯å¯è‡ªç”±ä¿®æ”¹çš„å†…å®¹æ ·å¼ç»„ä»¶ ===== */\n        /* å¤§æ¨¡å‹å¯ä»¥æ ¹æ®éœ€è¦å¢åŠ ã€ä¿®æ”¹è¿™éƒ¨åˆ†æ ·å¼ */\n        .content-block {\n            margin-bottom: var(--spacing-lg);\n            animation: fade-in 0.5s ease-out;\n        }\n        \n        @keyframes fade-in {\n            from { opacity: 0; transform: translateY(20px); }\n            to { opacity: 1; transform: translateY(0); }\n        }\n        \n        .title-block {\n            margin-bottom: 1.5rem;\n            position: relative;\n        }\n        \n        .title-block h1 {\n            font-size: 2.5rem;\n            line-height: 1.2;\n            margin-bottom: 0.5rem;\n        }\n        \n        .title-block h2 {\n            font-size: 2rem;\n            line-height: 1.3;\n            margin-bottom: 0.5rem;\n        }\n        \n        .title-block h3 {\n            font-size: 1.75rem;\n            line-height: 1.4;\n            margin-bottom: 0.5rem;\n        }\n        \n        .text-block {\n            font-size: 1.1rem;\n            line-height: 1.6;\n            margin-bottom: 1.5rem;\n        }\n    </style>\n</head>\n\n<body>\n    <!-- ã€å›ºå®šã€‘èƒŒæ™¯æ•ˆæœ -->\n    <div class=\"bg-particles\">\n        <canvas id=\"particleCanvas\"></canvas>\n        <canvas id=\"knowledgeGraph\"></canvas>\n    </div>\n    \n    <!-- ã€å›ºå®šã€‘é¡¶éƒ¨å¯¼èˆª -->\n    <header>\n        <div class=\"header-container\">\n            <div class=\"institute-branding\">\n                <div class=\"logo-container\">\n                    <img src=\"https://www.ailabpku.com/uploads/lessons/13/1746686579877-logo.png\" alt=\"å®éªŒå®¤logo\" style=\"width: 100%; height: 100%; object-fit: contain;\" onerror=\"this.src=\'data:image/svg+xml;charset=utf-8,%3Csvg xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22 viewBox%3D%220 0 40 40%22%3E%3Crect width%3D%2240%22 height%3D%2240%22 fill%3D%22%239E1B32%22%2F%3E%3Ctext x%3D%2250%25%22 y%3D%2250%25%22 font-size%3D%2220%22 text-anchor%3D%22middle%22 fill%3D%22white%22 dy%3D%22.3em%22%3EPK%3C%2Ftext%3E%3C%2Fsvg%3E\'\">\n                </div>\n                <div class=\"institute-title\">åŒ—äº¬å¤§å­¦äººå·¥æ™ºèƒ½åº”ç”¨ä¸åˆ›æ–°å®éªŒå®¤</div>\n            </div>\n        </div>\n    </header>\n    \n    <!-- å¯è‡ªç”±å®šåˆ¶çš„å†…å®¹åŒºåŸŸ -->\n    <main class=\"main-content\">\n        <section class=\"content-section\">\n            <!-- å¤§æ¨¡å