INSERT INTO `messages` (`id`, `conversation_id`, `role`, `content`, `tokens`, `model_name`, `file_id`, `created_at`) VALUES ('9961c059-9bb7-4c85-af2a-0ece3d7a5e46','081dcad5-73cb-4ca7-8ecb-588beb5bd769','assistant','è¿™é‡Œæ˜¯ä¸€ä¸ªç®€åŒ–ç‰ˆçš„æ‰“ç –å—æ¸¸æˆï¼š\n\n```html\n<!DOCTYPE html>\n<html>\n<head>\n    <title>ç®€å•æ‰“ç –å—</title>\n    <style>\n        body {\n            margin: 0;\n            padding: 20px;\n            text-align: center;\n            background: #222;\n            color: white;\n            font-family: Arial, sans-serif;\n        }\n        canvas {\n            border: 2px solid white;\n            background: black;\n        }\n        #info {\n            margin: 10px;\n            font-size: 20px;\n        }\n    </style>\n</head>\n<body>\n    <h1>ğŸ® ç®€å•æ‰“ç –å—</h1>\n    <div id=\"info\">åˆ†æ•°: <span id=\"score\">0</span> | ç”Ÿå‘½: <span id=\"lives\">3</span></div>\n    <canvas id=\"game\" width=\"600\" height=\"400\"></canvas>\n    <p>â† â†’ æ–¹å‘é”®ç§»åŠ¨ï¼Œç©ºæ ¼å¼€å§‹</p>\n\n    <script>\n        const canvas = document.getElementById(\'game\');\n        const ctx = canvas.getContext(\'2d\');\n\n        // æ¸¸æˆå˜é‡\n        let score = 0;\n        let lives = 3;\n        let gameRunning = false;\n\n        // æŒ¡æ¿\n        const paddle = {\n            x: 250,\n            y: 370,\n            width: 100,\n            height: 10,\n            speed: 6\n        };\n\n        // çƒ\n        const ball = {\n            x: 300,\n            y: 200,\n            radius: 8,\n            dx: 3,\n            dy: 3\n        };\n\n        // ç –å—\n        const bricks = [];\n        for (let i = 0; i < 5; i++) {\n            for (let j = 0; j < 8; j++) {\n                bricks.push({\n                    x: j * 75,\n                    y: i * 30 + 50,\n                    width: 70,\n                    height: 25,\n                    alive: true,\n                    color: `hsl(${i * 60}, 70%, 60%)`\n                });\n       