INSERT INTO `messages` (`id`, `conversation_id`, `role`, `content`, `tokens`, `model_name`, `file_id`, `created_at`) VALUES ('ae6ada8a-ed91-441b-a03f-6862d03f4d36','60213af2-b3c9-4974-9eee-16bbc50e82d9','assistant','ä½ é‡åˆ°çš„æŠ¥é”™æ˜¯å› ä¸º`ft_layout`è¿”å›çš„å­—å…¸å’Œ`update_layout`çš„å‚æ•°ä¸­éƒ½åŒ…å«äº†`legend`ï¼Œå¯¼è‡´`legend`å‚æ•°é‡å¤ä¼ é€’ã€‚  \n**è§£å†³æ–¹æ³•**ï¼š  \n- åªåœ¨`update_layout`ä¸­è®¾ç½®`legend`ï¼Œä¸è¦åœ¨`ft_layout`è¿”å›çš„å­—å…¸é‡Œè®¾ç½®`legend`ã€‚\n\næ­¤å¤–ï¼Œå»ºè®®å°†`xaxis`ã€`yaxis`ã€`yaxis2`ã€`legend`ç­‰éƒ½åªåœ¨`update_layout`ä¸­è®¾ç½®ï¼Œ`ft_layout`åªè´Ÿè´£å…¨å±€æ ·å¼ã€‚\n\n---\n\n## å®Œæ•´ä¿®æ­£ç‰ˆä»£ç \n\n```python\nimport pandas as pd\nimport dash\nfrom dash import dcc, html, Input, Output\nimport plotly.graph_objects as go\nimport plotly.express as px\n\n# 1. è¯»å–Excelæ•°æ®\nfile_path = r\'C:\\test\\test2024.xlsx\'\ndf = pd.read_excel(file_path, sheet_name=\'Sheet1\', header=None)\n\n# 2. æ•´ç†æ•°æ®\nmonths = df.loc[0, 1:12].tolist()  # B1-M1\ndata = {\n    \'æœˆä»½\': months,\n    \'GMV\': df.loc[1, 1:12].tolist(),\n    \'è®¢å•æ•°\': df.loc[2, 1:12].tolist(),\n    \'ç‚¹å‡»é‡\': df.loc[3, 1:12].tolist(),\n    \'è½¬åŒ–ç‡\': df.loc[4, 1:12].tolist()\n}\ndata_df = pd.DataFrame(data)\n\n# 3. Dashåº”ç”¨æ­å»º\napp = dash.Dash(__name__)\n\nmonth_options = [{\'label\': m, \'value\': m} for m in data_df[\'æœˆä»½\']]\n\n# FTé£æ ¼å‚æ•°\nft_font = \"Arial\"\nft_color = \"#222222\"\nft_gridcolor = \"#E5ECF6\"\nft_palette = [\'#E87722\', \'#262626\', \'#A7A9AC\']  # FTæ©™ã€æ·±ç°ã€æµ…ç°\n\ndef ft_layout(title):\n    return dict(\n        template=\'plotly_white\',\n        title=dict(text=title, font=dict(size=20, family=ft_font, color=ft_color), x=0.5),\n        font=dict(family=ft_font, size=14, color=ft_color),\n        plot_bgcolor=\'white\',\n        paper_bgcolor=\'white\',\n        margin=dict(l=60, r=40, t=60, b=60),\n    )\n\napp.layout = html.Div([\n    html.H1(\"ç”µå•†åº—é“ºå¹´åº¦è¿è¥BIé¢æ¿\", style={\'textAlign\': \'center\', \'fontFamily\': ft_font, \'color\': ft_color}),\n    html.Label(\"é€‰æ‹©æœˆä»½ï¼š\", style={\'fontFamily\': ft_font, \'fontSize\': 16}),\n    dcc.Checklist(\n        id=\'month-selector\',\n        options=month_options,\n        value=data_df[\'æœˆä»½\'].tolist(),  # é»˜è®¤å…¨é€‰\n        inline=True,\n        style={\'marginBottom\': \'20px\'}\n    ),\n    # ç¬¬ä¸€è¡Œï¼šGMVæŸ±çŠ¶å›¾å’Œé¥¼çŠ¶å›¾\n    html.Div([\n        html.Div([dcc.Graph(id=\'gmv-bar\')], style={\'width\': \'50%\'}),\n        html.Div([dcc.Graph(id=\'gmv-pie\')], style={\'width\': \'50%\'})\n    ], style={\'display\': \'flex\', \'flexDirection\': \'row\'}),\n    # ç¬¬äºŒè¡Œï¼šæŠ˜çº¿å›¾\n    html.Div([\n        dcc.Graph(id=\'line-chart\')\n    ], style={\'marginTop\': \'30px\'})\n], style={\'maxWidth\': \'1200px\', \'margin\': \'auto\', \'backgroundColor\': \'white\'})\n\n@app.callback(\n    Output(\'gmv-bar\', \'figure\'),\n    Output(\'gmv-pie\', \'figure\'),\n    Output(\'line-chart\', \'figure\'),\n    Input(\'month-selector\', \'value\')\n)\ndef update_charts(selected_months):\n    if not selected_months:\n        return {}, {}, {}\n    filtered = data_df[data_df[\'æœˆä»½\'].isin(selected_months)]\n\n    # GMVæŸ±çŠ¶å›¾\n    fig_bar = px.bar(\n        filtered, x=\'æœˆä»½\', y=\'GMV\',\n        color_discrete_sequence=[ft_palette[0]],\n        title=\'GMVåˆ†æœˆæŸ±çŠ¶å›¾\'\n    )\n    fig_bar.update_layout(\n        **ft_layout(\'GMVåˆ†æœˆæŸ±çŠ¶å›¾\'),\n        xaxis=dict(showgrid=True, gridcolor=ft_gridcolor, linecolor=ft_color, linewidth=1, ticks=\'outside\'),\n        yaxis=dict(showgrid=True, gridcolor=ft_gridcolor, linecolor=ft_color, linewidth=1, ticks=\'outside\'),\n        legend=dict(bgcolor=\'rgba(0,0,0,0)\', borderwidth=0, font=dict(size=13))\n    )\n\n    # GMVé¥¼çŠ¶å›¾\n    fig_pie = px.pie(\n        filtered, names=\'æœˆä»½\', values=\'GMV\',\n        color_discrete_sequence=px.colors.sequential.Oranges,\n        title=\'GMVåˆ†æœˆé¥¼çŠ¶å›¾\',\n        hole=0.3\n    )\n    fig_pie.update_traces(