INSERT INTO `messages` (`id`, `conversation_id`, `role`, `content`, `tokens`, `model_name`, `file_id`, `created_at`) VALUES ('dede7d7c-529a-4088-91b5-81fdbd251fa4','488300e8-8d42-4f37-b43b-3a5f1435d25d','assistant','éå¸¸æ„Ÿè°¢ä½ çš„è¯¦ç»†è¯´æ˜ï¼  \n**ä½ çš„è®¢å•è¡¨â€œè®¢å•æ—¶é—´â€åˆ—ï¼Œå•å…ƒæ ¼å®é™…å­˜å‚¨ä¸ºæ—¥æœŸæ ¼å¼ï¼ˆå¦‚2022/1/2ï¼‰ï¼Œåªæ˜¯Excelæ˜¾ç¤ºä¸ºâ€œ2022å¹´1æœˆ2æ—¥â€ã€‚**  \nè¿™æ„å‘³ç€**pandasè¯»å–æ—¶ä¼šè‡ªåŠ¨è¯†åˆ«ä¸º`datetime`ç±»å‹æˆ–å­—ç¬¦ä¸²â€œYYYY/M/Dâ€**ï¼Œè€Œä¸æ˜¯â€œ2022å¹´1æœˆ2æ—¥â€è¿™ç§ä¸­æ–‡æ ¼å¼ã€‚\n\n### æ­£ç¡®å¤„ç†æ–¹å¼\n\n1. **ç›´æ¥ç”¨`pd.to_datetime`è§£æï¼Œä¸éœ€è¦æŒ‡å®š`format`**ï¼Œè®©pandasè‡ªåŠ¨è¯†åˆ«å³å¯ã€‚\n2. **ç­›é€‰æ—¶ç›´æ¥ç”¨`.dt.year`å’Œ`.dt.month`**ã€‚\n\n### æ¨èä»£ç \n\n```python\nimport pandas as pd\nimport tkinter as tk\nfrom tkinter import ttk\nfrom tkinter import messagebox\n\ndef select_year_month():\n    def on_ok():\n        nonlocal selected_year, selected_month\n        selected_year = year_var.get()\n        selected_month = month_var.get()\n        root.destroy()\n\n    selected_year = None\n    selected_month = None\n\n    root = tk.Tk()\n    root.title(\"é€‰æ‹©å¹´å’Œæœˆ\")\n    tk.Label(root, text=\"é€‰æ‹©å¹´ä»½:\").grid(row=0, column=0)\n    tk.Label(root, text=\"é€‰æ‹©æœˆä»½:\").grid(row=1, column=0)\n\n    year_var = tk.StringVar()\n    month_var = tk.StringVar()\n\n    years = [str(y) for y in range(2022, 2024)]\n    months = [str(m).zfill(2) for m in range(1, 13)]\n\n    year_cb = ttk.Combobox(root, textvariable=year_var, values=years, state=\"readonly\")\n    month_cb = ttk.Combobox(root, textvariable=month_var, values=months, state=\"readonly\")\n    year_cb.grid(row=0, column=1)\n    month_cb.grid(row=1, column=1)\n\n    tk.Button(root, text=\"ç¡®å®š\", command=on_ok).grid(row=2, column=0, columnspan=2)\n    root.mainloop()\n    return selected_year, selected_month\n\n# é€‰æ‹©å¹´æœˆ\nyear, month = select_year_month()\nif not year or not month:\n    messagebox.showerror(\"é”™è¯¯\", \"æœªé€‰æ‹©å¹´ä»½æˆ–æœˆä»½ï¼Œç¨‹åºç»ˆæ­¢ã€‚\")\n    exit()\n\n# æ–‡ä»¶è·¯å¾„\norder_file = r\"c:\\xiaoshou\\è®¢å•è¡¨.xlsx\"\ndict_file = r\"c:\\xiaoshou\\å­—å…¸.xlsx\"\noutput_file = rf\"c:\\xiaoshou\\è®¢å•è¡¨_{year}{month}.xlsx\"\n\n# è¯»å–æ•°æ®\norders = pd.read_excel(order_file, sheet_name=\"Sheet1\", dtype={\"äº§å“ID\": str})\nproducts = pd.read_excel(dict_file, sheet_name=\"Sheet1\", dtype={\"ID\": str})\nregions = pd.read_excel(dict_file, sheet_name=\"Sheet2\", header=None, names=[\"å¤§åŒº\", \"å§“å\"])\n\n# è®¢å•æ—¶é—´æ ¼å¼å¤„ç†ï¼ˆè‡ªåŠ¨è¯†åˆ«ï¼‰\norders[\"è®¢å•æ—¶é—´\"] = pd.to_datetime(orders[\"è®¢å•æ—¶é—´\"], errors=\"coerce\")\n\n# æ£€æŸ¥è§£æç»“æœ\n#print(orders[\"è®¢å•æ—¶é—´\"].head(10))\n\n# ç­›é€‰æŒ‡å®šå¹´æœˆ\norders_filtered = orders[\n    (orders[\"è®¢å•æ—¶é—´\"].dt.year == int(year)) & \n    (orders[\"è®¢å•æ—¶é—´\"].dt.month == int(month))\n]\n\n# åˆå¹¶äº§å“æˆæœ¬\norders_filtered = orders_filtered.merge(\n    products[[\"ID\", \"æˆæœ¬\"]], left_on=\"äº§å“ID\", right_on=\"ID\", how=\"left\"\n)\norders_filtered.drop(\"ID\", axis=1, inplace=True)\n\n# åˆå¹¶é”€å”®å‘˜å¤§åŒº\norders_filtered = orders_filtered.merge(\n    regions, left_on=\"é”€å”®å‘˜\", right_on=\"å§“å\", how=\"left\"\n)\norders_filtered.drop(\"å§“å\", axis=1, inplace=True)\n\n# ä¿å­˜åˆ°æ–°æ–‡ä»¶\norders_filtered.to_excel(output_file, index=False)\n\nif len(orders_filtered) == 0:\n    messagebox.showwarning(\"æç¤º\", f\"æ‰€é€‰{year}å¹´{month}æœˆæ— è®¢å•æ•°æ®ã€‚\")\nelse:\n    messagebox.showinfo(\"å®Œæˆ\", f\"å·²ç”Ÿæˆæ–‡ä»¶ï¼š{output_file}\")\n```\n\n---\n\n## é‡ç‚¹è¯´æ˜\n- **ä¸éœ€è¦æŒ‡å®š`format`**ï¼Œè®©pandasè‡ªåŠ¨è¯†åˆ«æ—¥æœŸæ ¼å¼ã€‚\n- **ç­›é€‰æ¡ä»¶ç›´æ¥ç”¨`.dt.year`å’Œ`.dt.month`**ã€‚\n- è¿™æ ·å¯ä»¥å…¼å®¹Excelä¸­æ—¥æœŸå•å…ƒæ ¼çš„å„ç§æ˜¾ç¤ºæ–¹å¼ã€‚\n\nå¦‚è¿˜æœ‰é—®é¢˜ï¼Œè¯·è´´å‡º`print(orders[\"è®¢å•æ—¶é—´\"].head(10))`çš„è¾“å‡ºç»“æœï¼Œæˆ‘å¸®ä½ è¿›ä¸€æ­¥å®šä½ï¼',1314,'gpt-4.1',NULL,'2025-07-29 07:05:42');
