INSERT INTO `messages` (`id`, `conversation_id`, `role`, `content`, `tokens`, `model_name`, `file_id`, `created_at`) VALUES ('f1bf5f91-c4c9-4578-bbad-ca633e502f6b','3d8656e3-199c-46e4-8e9b-7e81d5e13dc5','assistant','å¥½çš„ï¼ä¸‹é¢ç»™ä½ ä¸€ä¸ªæ›´å®Œæ•´ä¸€ç‚¹çš„**PHPå‘½ä»¤è¡Œä¿„ç½—æ–¯æ–¹å—**ç¤ºä¾‹ï¼ŒåŒ…å«äº†ï¼š\n\n- æ–¹å—çš„å·¦å³ç§»åŠ¨\n- æ—‹è½¬\n- å¿«é€Ÿä¸‹è½\n- æ¶ˆè¡Œ\n- æ¸¸æˆç»“æŸåˆ¤æ–­\n- ç®€å•çš„é”®ç›˜è¾“å…¥ï¼ˆé€šè¿‡ `readline()`ï¼‰\n\nç”±äºPHPå‘½ä»¤è¡Œå¯¹å®æ—¶é”®ç›˜ç›‘å¬æ”¯æŒæœ‰é™ï¼Œè¿™é‡Œç”¨çš„æ˜¯æ¯æ¬¡å¾ªç¯ç­‰å¾…ç”¨æˆ·è¾“å…¥ä¸€ä¸ªå‘½ä»¤ï¼ˆa:å·¦ç§»ï¼Œd:å³ç§»ï¼Œw:æ—‹è½¬ï¼Œs:å¿«é€Ÿä¸‹è½ï¼Œq:é€€å‡ºï¼‰ï¼Œæ¨¡æ‹Ÿç®€å•äº¤äº’ã€‚\n\n```php\n<?php\n// ä¿„ç½—æ–¯æ–¹å—å‘½ä»¤è¡Œç‰ˆï¼ˆå¸¦ç®€å•äº¤äº’ï¼‰\n\n// æ–¹å—å½¢çŠ¶å®šä¹‰\n$shapes = [\n    [[1, 1, 1, 1]], // I\n    [[1, 1], [1, 1]], // O\n    [[0, 1, 0], [1, 1, 1]], // T\n    [[1, 1, 0], [0, 1, 1]], // S\n    [[0, 1, 1], [1, 1, 0]], // Z\n    [[1, 0, 0], [1, 1, 1]], // J\n    [[0, 0, 1], [1, 1, 1]], // L\n];\n\n// æ£‹ç›˜å¤§å°\n$width = 10;\n$height = 20;\n\n// åˆå§‹åŒ–æ£‹ç›˜\n$board = array_fill(0, $height, array_fill(0, $width, 0));\n\n// ç”Ÿæˆæ–°æ–¹å—\nfunction newBlock($shapes) {\n    $shape = $shapes[array_rand($shapes)];\n    return [\'shape\' => $shape, \'x\' => 3, \'y\' => 0];\n}\n\n// æ£€æŸ¥æ–¹å—æ˜¯å¦èƒ½æ”¾ç½®\nfunction canPlace($board, $block, $x, $y) {\n    $shape = $block[\'shape\'];\n    foreach ($shape as $i => $row) {\n        foreach ($row as $j => $cell) {\n            if ($cell) {\n                $bx = $x + $j;\n                $by = $y + $i;\n                if ($bx < 0 || $bx >= count($board[0]) || $by < 0 || $by >= count($board)) return false;\n                if ($board[$by][$bx]) return false;\n            }\n        }\n    }\n    return true;\n}\n\n// å°†æ–¹å—å›ºå®šåˆ°æ£‹ç›˜\nfunction placeBlock(&$board, $block) {\n    $shape = $block[\'shape\'];\n    $x = $block[\'x\'];\n    $y = $block[\'y\'];\n    foreach ($shape as $i => $row) {\n        foreach ($row as $j => $cell) {\n            if ($cell) {\n                $board[$y + $i][$x + $j] = 1;\n            }\n        }\n    }\n}\n\n// æ¶ˆé™¤æ»¡è¡Œ\nfunction clearLines(&$board) {\n    $linesCleared = 0;\n    for ($i = count($board) - 1; $i >= 0; $i--) {\n        if (!in_array(0, $board[$i])) {\n            array_splice($board, $i, 1);\n            array_unshift($board, array_fill(0, count($board[0]), 0));\n            $linesCleared++;\n            $i++; // é‡æ–°æ£€æŸ¥å½“å‰è¡Œ\n        }\n    }\n    return $linesCleared;\n}\n\n// æ—‹è½¬æ–¹å—ï¼ˆé¡ºæ—¶é’ˆï¼‰\nfunction rotate($shape) {\n    $rotated = [];\n    $rows = count($shape);\n    $cols = count($shape[0]);\n    for ($j = 0; $j < $cols; $j++) {\n        $newRow = [];\n        for ($i = $rows - 1; $i >= 0; $i--) {\n            $newRow[] = $shape[$i][$j];\n        }\n        $rotated[] = $newRow;\n    }\n    return $rotated;\n}\n\n// æ‰“å°æ£‹ç›˜å’Œå½“å‰æ–¹å—\nfunction printBoard($board, $block = null, $score = 0) {\n    system(\'clear\'); // æ¸…å±ï¼ˆLinux/macOSï¼‰ï¼ŒWindowsç”¨ system(\'cls\');\n    $display = $board;\n    if ($block) {\n        $shape = $block[\'shape\'];\n        $x = $block[\'x\'];\n        $y = $block[\'y\'];\n        foreach ($shape as $i => $row) {\n            foreach ($row as $j => $cell) {\n                if ($cell && isset($display[$y + $i][$x + $j])) {\n                    $display[$y + $i][$x + $j] = 2;\n                }\n            }\n        }\n    }\n    echo \"Score: $score\\n\";\n    echo \"+------------+\\n\";\n    foreach ($display as $row) {\n        echo \"|\";\n        foreach ($row as $cell) {\n            if ($cell == 0) echo \"  \";\n            elseif ($cell == 1) echo \"[]\";\n            elseif ($cell == 2) echo \"<>\";\n        }\n        echo \"|\\n\";\n    }\n    echo \"+------------+\\n\";\n    echo \"Controls: a=left, d=right, w=rotate, s=down, q=quit\\n\";\n}\n\n// ä¸»æ¸¸æˆå¾ªç¯\n$block = newBlock($shapes);\n$score = 0;\n$gameOver = false;\n\nwhile (!$gameOver) {\n    printBoard($board, $block, $score);\n\n    // è¯»å–ç”¨æˆ·è¾“å…¥\n    $input = readline(\"Your move: \");\n    $input = strtolower(trim($input));\n\n    if ($input === \'q\') {\n        echo \"Game quit. Final score: $score\\n\";\n        break;\n    }\n\n    // å¤„ç†è¾“å…¥\n    if ($input === \'a\') {\n        // å·¦ç§»\n        if (canPlace($board, $block, $block[\'x\'] - 1, $block[\'y\'])) {\n            $block[\'x\']--;\n        }\n    } elseif ($input === \'d\') {\n        // å³ç§»\n        if (canPlace($board, $block, $block[\'x\'] + 1, $block[\'y\'])) {\n            $block[\'x\']++;\n        }\n    } elseif ($input === \'w\') {\n        // æ—‹è½¬\n        $rotated = rotate($block[\'shape\']);\n        if (canPlace($board, [\'shape\' => $rotated], $block[\'x\'], $block[\'y\'])) {\n            $block[\'shape\'] = $rotated;\n        }\n    } elseif ($input === \'s\') {\n        // å¿«é€Ÿä¸‹è½\n        while (canPlace($board, $block, $block[\'x\'], $block[\'y\'] + 1)) {\n            $block[\'y\']++;\n        }\n    } else {\n        // æ— æ•ˆè¾“å…¥ï¼Œå¿½ç•¥\n    }\n\n    // æ–¹å—è‡ªåŠ¨ä¸‹è½ä¸€æ ¼ï¼ˆé™¤éåˆšåˆšå¿«é€Ÿä¸‹è½ï¼‰\n    if ($input !== \'s\') {\n        if (canPlace($board, $block, $block[\'x\'], $block[\'y\'] + 1)) {\n            $block[\'y\']++;\n        } else {\n            // å›ºå®šæ–¹å—\n            placeBlock($board, $block);\n            $lines = clearLines($board);\n            $score += $lines * 100;\n            $block = newBlock($shapes);\n            if (!canPlace($board, $block, $block[\'x\'], $block[\'y\'])) {\n                $gameOver = true;\n            }\n        }\n    }\n}\n\nif ($gameOver) {\n    printBoard($board, null, $score);\n    echo \"Game Over! Your final score: $score\\n\";\n}\n```\n\n---\n\n### ä½¿ç”¨è¯´æ˜\n\n1. å°†ä»£ç ä¿å­˜ä¸º `tetris.php`\n2. åœ¨å‘½ä»¤è¡Œè¿è¡Œï¼š`php tetris.php`\n3. æŒ‰æç¤ºè¾“å…¥å‘½ä»¤ï¼š\n   - `a` å·¦ç§»\n   - `d` å³ç§»\n   - `w` æ—‹è½¬\n   - `s` å¿«é€Ÿä¸‹è½åˆ°åº•éƒ¨\n   - `q` é€€å‡ºæ¸¸æˆ\n\n---\n\n### è¯´æ˜\n\n- è¿™æ˜¯ä¸€ä¸ªåŸºäºå‘½ä»¤è¡Œçš„ç®